import React, { Component } from 'react';
import AddTaskForm from './components/AddTaskForm';
import EditTaskForm from './components/EditTaskForm';
import FilterTasks from './components/FilterTasks';
import TaskList from './components/TaskList';
import './index.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      filter: '',
      editingTask: null,
    };
  }

  componentDidMount() {
    this.fetchTasks();
  }

  fetchTasks = () => {
    fetch('http://localhost:5000/tasks')
      .then(response => response.json())
      .then(data => {
        this.setState({ tasks: data });
      })
      .catch(error => console.error('Erro ao obter a lista de tarefas:', error));
  };

  addTask = (task) => {
    fetch('http://localhost:5000/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(task),
    })
    .then(response => response.json())
    .then(data => {
      this.setState((prevState) => ({
        tasks: [...prevState.tasks, data]
      }));
    })
    .catch(error => console.error('Erro ao adicionar a tarefa:', error));
  };

  updateTask = (updatedTask) => {
    fetch(`http://localhost:5000/tasks/${updatedTask._id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedTask),
    })
    .then(response => response.json())
    .then(data => {
      this.setState((prevState) => ({
        tasks: prevState.tasks.map(task => 
          task._id === data._id ? data : task
        ),
        editingTask: null,
      }));
    })
    .catch(error => console.error('Erro ao atualizar a tarefa:', error));
  };

  deleteTask = (id) => {
    fetch(`http://localhost:5000/tasks/${id}`, {
      method: 'DELETE',
    })
    .then(() => {
      this.setState((prevState) => ({
        tasks: prevState.tasks.filter(task => task._id !== id),
      }));
    })
    .catch(error => console.error('Erro ao excluir a tarefa:', error));
  };

  completeTask = (id) => {
    const taskToComplete = this.state.tasks.find(task => task._id === id);
    const updatedTask = { ...taskToComplete, completed: true };

    this.updateTask(updatedTask);
  };

  setFilter = (filter) => {
    this.setState({ filter });
  };

  editTask = (task) => {
    this.setState({ editingTask: task });
  };

  cancelEdit = () => {
    this.setState({ editingTask: null });
  };

  render() {
    const { tasks, filter, editingTask } = this.state;
    const filteredTasks = tasks.filter(task => 
      task.title.toLowerCase().includes(filter.toLowerCase())
    );

    return (
      <div className="App">
        <h1>Lista de Tarefas</h1>
        <FilterTasks filter={filter} setFilter={this.setFilter} />
        {editingTask ? (
          <EditTaskForm 
            task={editingTask} 
            updateTask={this.updateTask} 
            cancelEdit={this.cancelEdit} 
          />
        ) : (
          <AddTaskForm addTask={this.addTask} />
        )}
        <TaskList 
          tasks={filteredTasks} 
          editTask={this.editTask} 
          deleteTask={this.deleteTask} 
          completeTask={this.completeTask} 
        />
      </div>
    );
  }
}

export default App;
