import React from 'react';

const TaskList = ({ tasks, deleteTask, startEditing, completeTask }) => {
  return (
    <div className="task-list">
      {tasks.map((task) => (
        <div key={task._id} className="task-item">
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p>Data: {new Date(task.dueDate).toLocaleDateString()}</p> {/* Correção aqui */}
          <p>Importância: {task.priority}</p>
          <p>Categoria: {task.category}</p>
          <p>Status: {task.completed ? 'Completado' : 'Incompleto'}</p>
          <div className="buttons">
            <button onClick={() => completeTask(task._id)} className="complete">
              {task.completed ? 'Desmarcar' : 'Completado'}
            </button>
            <button onClick={() => startEditing(task)} className="edit">
              Editar
            </button>
            <button onClick={() => deleteTask(task._id)} className="delete">
              Excluir
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TaskList;
