import React from 'react';

const TaskList = ({ tasks, editTask, deleteTask, completeTask }) => {
  return (
    <ul>
      {tasks.map(task => (
        <li key={task._id}>
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p>Vencimento: {new Date(task.dueDate).toLocaleDateString()}</p>
          <p>Prioridade: {task.priority}</p>
          <p>Categoria: {task.category}</p>
          <button onClick={() => editTask(task)}>Editar</button>
          <button onClick={() => deleteTask(task._id)}>Excluir</button>
          <button onClick={() => completeTask(task._id)}>Completar</button>
        </li>
      ))}
    </ul>
  );
};

export default TaskList;
