import React from 'react';

const TaskList = ({ tasks, editTask, deleteTask, completeTask }) => {
  return (
    <div className="task-list">
      {tasks.map((task) => (
        <div className="task-item" key={task._id}>
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <div>
            <button className="complete" onClick={() => completeTask(task._id)}>Complete</button>
            <button className="edit" onClick={() => editTask(task)}>Edit</button>
            <button className="delete" onClick={() => deleteTask(task._id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TaskList;
